import React, { useEffect, useState, useMemo } from "react";
import api from '../services/api';
import { Column, CellProps, useTable } from 'react-table';
import { organizeClientsAccounts } from "../helpers/clients";

export const Bank = () => {
  const [transactions, setTransactions] = useState([]);
  const [clients, setClients] = useState([]);

  useEffect(async () => {
    const {data} = await api.get('transactions.json');
    setTransactions(data)
    setClients(organizeClientsAccounts(data));

  }, []);


  const columns = useMemo(
    () => [
      {
        Header: "Customer's name",
        colSpan: 1,
        accessor: 'name',
      },
      {
        Header: "Checking account total",
        accessor: 'checking',
        colSpan: 2,
      },
      {
        Header: "Saving account total",
        accessor: 'saving',
        colSpan: 1,
      },
      {
        Header: "Total savings + checking account",
        accessor: 'productCategory',
        Cell: ( row  => {
          return (row.savings + row.checking).toFixed(2) 
        }),
        colSpan: 2,
      },
    ],
    [],
  );

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    page
  } = useTable(
    {
      columns,
      data: clients,
    },
  );

  return (
    <table {...getTableProps()}>
      {headerGroups.map(headerGroup => (
        <thead
          {...headerGroup.getHeaderGroupProps()}
          key={`${headerGroup.id}hg`}
        >
          {headerGroup.headers.map(column => (
            <tr
              {...column.getHeaderProps()}
              key={`${column.id}col`}
            >
              <th>
                {column.render('Header')}
              </th>
            </tr>
          ))}
        </thead>
      ))}

      <tbody {...getTableBodyProps()}>
        {page.map((row) => {
          prepareRow(row);
          return (
            <tr
              key={`${row.id}row`}
            >
              {row.cells.map(cell => {
                return (
                  <React.Fragment key={`${cell.column.id}Heading`}>
                    <td
                    >
                        {cell.column.Header}
                    </td>
                    <td
                      {...row.getRowProps()}
                      as="td"
                    >
                        {cell.render('Cell')}
                    </td>
                  </React.Fragment>
                );
              })}
            </tr>
          );
        })}
      </tbody>
  </table>
  )
};
