export const organizeClientsAccounts = (transactions) => {
  const clients = [];

  transactions.forEach(element => {
    if (clients.findIndex(item => item.id === element.customer_id)){
      /*const newClients = clients.map(item => {
        if (item.id === element.customer_id) {
          if(element.account_type === "savings") {
            return {...item, savings: item.savings + getTransactionNumber(transaction_amount)};
          }else  {
            return {...item, savings: item.checking + getTransactionNumber(transaction_amount)};
          }          
        }
        return item;
      });
      clients = newClients;*/
    } else {
      const client = {
        id: element.customer_id,
        name: element.customer_name,
        email: element.customer_email,
        savings: element.account_type === "savings" && getTransactionNumber(element.transaction_amount),
        checking: element.account_type === "checking" && getTransactionNumber(element.transaction_amount),
      }
      clients.push(client);
    }
  });
  console.log("clients before", clients)
  return clients;
}

export const getTransactionNumber = (string) => {
  console.log("conver string to number", Number(string.replace(/$/ig, '')))
  return Number(string.replace(/$/ig, ''));
}
