import React, { useEffect, useState, useCallback } from "react";
import api from '../services/api';
import { Column, CellProps } from 'react-table';
import { organizeClientsAccounts } from "../helpers/clients";

export const Bank = () => {
  const [transactions, setTransactions] = useState([]);
  const [clients, setClients] = useState([]);

  useEffect(async () => {
    const {data} = await api.get('transactions.json');
    setTransactions(data)
    setClients(organizeClientsAccounts(data));

  }, []);


  const columns = useMemo(
    () => [
      {
        Header: "Customer's name",
        colSpan: 1,
        accessor: row => row.name,
      },
      {
        Header: "Checking account total",
        accessor: row => row.checking,
        colSpan: 2,
      },
      {
        Header: "Saving account total",
        accessor: 'productType',
        Cell: ({ row }: CellProps<IContent>) => {
          return getProductTypeName(
            row.original.productCategory,
            row.original.productType,
          );
        },
        colSpan: 1,
      },
      {
        Header: "Total savings + checking account",
        accessor: 'productCategory',
        Cell: ({ row }: CellProps<IContent>) => {
          return t(`home:products.categories.${row.original.productCategory}.name`);
        },
        colSpan: 2,
      },
    ],
    [],
  );

  return <p>Replace this with your summary table</p>;

};
